mapper.select:
select a.*,b.examine_status,b.record_id as examine_record_id,
b.examine_step_id ,c.title as
categoryTitle,c.examine_type,c.icon
from wk_oa_examine a left join wk_oa_examine_record b on a.examine_id = b.examine_id left join
wk_oa_examine_category c on a.category_id = c.category_id
where


        1=1


        a.create_user_id = #{userId}



    and a.category_id = #{data.categoryId}




            and (b.examine_status = 0 or b.examine_status = 3)


            and b.examine_status = #{data.status}




    and b.examine_status != #{biStatus}


    and a.create_time between #{data.startTime} and #{data.endTime}

group by a.examine_id,b.record_id order by a.create_time desc

SELECT a.examine_user FROM wk_oa_examine_log as a WHERE a.record_id=#{record.examineRecordId} and is_recheck = 0 and
examine_status= 0
examine_status=#{record.examineStatus}
and a.examine_step_id=#{record.examineStepId}

select a.*,b.title as categoryTitle,b.type,b.icon,c.examine_status  from wk_oa_examine as a
left join wk_oa_examine_category b on a.category_id = b.category_id
left join `wk_oa_examine_record` c on a.examine_id = c.examine_id where a.examine_id = #{id}

SELECT saer.* ,sau.img,sau.realname from wk_oa_examine_record as  saer
LEFT JOIN wk_admin_user as sau on sau.user_id = saer.create_user
WHERE saer.record_id = #{recordId}

select a.examine_id,a.batch_id
    from wk_oa_examine a left join wk_oa_examine_record b on a.examine_id = b.examine_id left join wk_oa_examine_category c on a.category_id = c.category_id
    where


            1=1


            a.create_user_id = #{userId}



        and a.category_id = #{data.categoryId}




                and  (b.examine_status = 0 or b.examine_status = 3)


                and b.examine_status = #{data.checkStatus}




        and a.create_time between #{data.startTime} and  #{data.endTime}

    group by a.examine_id,b.record_id order by  a.create_time desc

select a.examine_id,a.batch_id
  from wk_oa_examine a  left join  wk_oa_examine_record b on a.examine_id = b.examine_id left join wk_oa_examine_category c on a.category_id = c.category_id left join wk_oa_examine_log d on d.record_id = b.record_id
    where 1 = 1

        and a.category_id = #{data.categoryId}


        and ((d.examine_user = #{userId} and d.examine_status = 0 and ifnull(b.examine_step_id,1) = ifnull(d.examine_step_id,1) and d.is_recheck !=1) or ((d.examine_user = #{userId} and d.examine_status not in(0,4))))


        and (d.examine_user = #{userId} and d.examine_status = 0 and ifnull(b.examine_step_id,1) = ifnull(d.examine_step_id,1) and d.is_recheck !=1)


        and (d.examine_user = #{userId} and d.examine_status not in(0,4))


        and a.create_time between #{data.startTime} and  #{data.endTime}


        and a.create_user_id = #{data.createUserId}

    group by a.examine_id,b.examine_status,b.record_id

select a.*,b.*,d.realname as create_user_name, (CASE WHEN f.examine_status = 0 THEN '未审核' WHEN f.examine_status = 1 THEN '审核通过' WHEN f.examine_status = 2 THEN '审核拒绝' WHEN f.examine_status = 3 THEN '审核中' WHEN f.examine_status = 4 THEN '已撤回' END ) as examine_status, (CASE WHEN f.examine_status in (0,3) THEN (select GROUP_CONCAT(realname) from wk_admin_user where user_id in ( select examine_user from wk_oa_examine_log where record_id = g.record_id and order_id = a.g.order_id)) WHEN f.examine_status in (1,2,4) THEN '' END ) as examine_user_name, (select GROUP_CONCAT(realname) from wk_admin_user where user_id in ( select examine_user from wk_oa_examine_log where record_id = g.record_id and order_id = (CASE WHEN f.examine_status in (0,3) THEN (g.order_id - 1) WHEN f.examine_status in (1,2,4) THEN g.order_id END) )) as previous_examine_user_name, c.customer_ids, c.contacts_ids, c.business_ids, c.contract_ids, if(c.customer_ids is null,null,(select GROUP_CONCAT(customer_name SEPARATOR'】、【') from wk_crm_customer where find_in_set(customer_id,c.customer_ids))) as customer_name, if(c.contacts_ids is null,null,(select GROUP_CONCAT(name SEPARATOR'】、【') from wk_crm_contacts where find_in_set(contacts_id,c.contacts_ids))) as contacts_name, if(c.business_ids is null,null,(select GROUP_CONCAT(business_name SEPARATOR'】、【') from wk_crm_business where find_in_set(business_id,c.business_ids))) as business_name, if(c.contract_ids is null,null,(select GROUP_CONCAT(name SEPARATOR'】、【') from wk_crm_contract where find_in_set(contract_id,c.contract_ids))) as contract_name from wk_oa_examine as a left join (select x.batch_id
,GROUP_CONCAT(if(name = '${field.name}',(select name from wk_admin_dept where find_in_set(dept_id,ifnull(x.vlaue,0))),null)) AS '${field.name}'
,GROUP_CONCAT(if(name = '${field.name}',(select realname from wk_admin_user where find_in_set(user_id,ifnull(x.vlaue,0))),null)) AS '${field.name}'
,max(CASE WHEN `name` = '${field.name}' THEN value END) AS '${field.name}'
from wk_oa_examine_data as x where x.batch_id in
#{batchId}
) as b on a.batch_id = b.batch_id left join wk_oa_examine_relation as c on a.examine_id = c.examine_id left join wk_admin_user as d on a.create_user_id = d.user_id left join wk_oa_examine_category as e on a.category_id = e.category_id left join wk_oa_examine_record as f on f.examine_id = a.examine_id left join (select max(order_id) as order_id,record_id from wk_oa_examine_log where is_recheck != 1 group by record_id) as g on f.record_id = g.record_id where a.examine_id in
#{id}

select a.*,d.realname as create_user_name, (CASE WHEN f.examine_status = 0 THEN '未审核' WHEN f.examine_status = 1 THEN '审核通过' WHEN f.examine_status = 2 THEN '审核拒绝' WHEN f.examine_status = 3 THEN '审核中' WHEN f.examine_status = 4 THEN '已撤回' END ) as examine_status, (CASE WHEN f.examine_status in (0,3) THEN (select GROUP_CONCAT(realname) from wk_admin_user where user_id in ( select examine_user from wk_oa_examine_log where record_id = g.record_id and order_id = a.g.order_id)) WHEN f.examine_status in (1,2,4) THEN '' END ) as examine_user_name, (select GROUP_CONCAT(realname) from wk_admin_user where user_id in ( select examine_user from wk_oa_examine_log where record_id = g.record_id and order_id = (CASE WHEN f.examine_status in (0,3) THEN (g.order_id - 1) WHEN f.examine_status in (1,2,4) THEN g.order_id END) )) as previous_examine_user_name, c.customer_ids, c.contacts_ids, c.business_ids, c.contract_ids, if(c.customer_ids is null,null,(select GROUP_CONCAT(customer_name SEPARATOR'】、【') from wk_crm_customer where find_in_set(customer_id,c.customer_ids))) as customer_name, if(c.contacts_ids is null,null,(select GROUP_CONCAT(name SEPARATOR'】、【') from wk_crm_contacts where find_in_set(contacts_id,c.contacts_ids))) as contacts_name, if(c.business_ids is null,null,(select GROUP_CONCAT(business_name SEPARATOR'】、【') from wk_crm_business where find_in_set(business_id,c.business_ids))) as business_name, if(c.contract_ids is null,null,(select GROUP_CONCAT(name SEPARATOR'】、【') from wk_crm_contract where find_in_set(contract_id,c.contract_ids))) as contract_name from wk_oa_examine as a left join wk_oa_examine_relation as c on a.examine_id = c.examine_id left join wk_admin_user as d on a.create_user_id = d.user_id left join wk_oa_examine_category as e on a.category_id = e.category_id left join wk_oa_examine_record as f on f.examine_id = a.examine_id left join (select max(order_id) as order_id,record_id from wk_oa_examine_log where is_recheck != 1 group by record_id) as g on f.record_id = g.record_id where a.examine_id in
#{id}
order by a.create_time
